Method.Source:
            public static void main(Args _args)
    {
        
        PKR_CustTransController   controller = new PKR_CustTransController();

        controller.parmReportName(ssrsReportStr(PKR_CustTransReport, CustomerDesign));
        controller.parmArgs(_args);
        controller.startOperation();
    }

    /// <summary>
    ///
    /// </summary>
    protected void preRunModifyContract()
    {
        PKR_CustTransContract contract = this.parmReportContract().parmRdpContract() as PKR_CustTransContract;


        if (contract.parmCustGroup() != '')
        {
            this.parmReportContract().parmReportName(ssrsReportStr(PKR_CustTransReport,GroupDesign));
        }
        else
        {
            this.parmReportContract().parmReportName(ssrsReportStr(PKR_CustTransReport,CustomerDesign ));

        }
        
        
        //super();
    }
