public class PKR_CustTransDP extends SRSReportDataProviderBase
{
    PKR_CustTransTmp tmp;
    CustAccount     custAccount;
    CustGroupId       custGroup;

}

Method.Source:
[SrsReportDataSetAttribute(tablestr(PKR_CustTransTmp))]
public PKR_CustTransTmp getCustTransTmp()
{
    select tmp;
    return tmp;
}

/// <summary>
///
/// </summary>
public void processReport()
{
    PKR_CustTransContract contract  = this.parmDataContract() as  PKR_CustTransContract;
    custAccount                     = contract.parmCustAccount();
    custGroup                       = contract.parmCustGroup();
     
    this.populateCustTrans();
}

public void populateCustTrans()
{
    CustTrans           custTrans;
    CustAccountName     custAccountName;
    CustTable           custTable;
    insert_recordset tmp(AccountNum,AmountCur,AmountMST,CurrencyCode,DocumentDate,DocumentNum,Invoice,TransDate,TransType ,Txt , Voucher, CustName, CustGroup)
        select AccountNum,AmountCur,AmountMST,CurrencyCode,DocumentDate,DocumentNum,Invoice,TransDate,TransType ,Txt , Voucher 
        from custTrans
        where (custAccount == '' || custTrans.AccountNum == custAccount )
        join Name from custAccountName
        where custAccountName.AccountNum == custTrans.AccountNum
        join CustGroup from custTable
        where custTable.AccountNum == custTrans.AccountNum
        && (custGroup == '' || custTable.CustGroup == custGroup);
}
